function abreOuFechaResposta(t){t.target.parentNode.classList.toggle("faq__questions__item--is-open")}function removeActiveButton(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].classList.remove("shows__tabs__button--is-active")}function hideAll(){var e=document.querySelectorAll("[data-tab-id]");for(let t=0;t<e.length;t++)e[t].classList.remove("shows__list--is-active")}document.addEventListener("DOMContentLoaded",function(){var e=document.querySelectorAll("[data-tab-button]"),o=document.querySelectorAll("[data-faq-question]");let t=document.querySelector(".hero").clientHeight;window.addEventListener("scroll",function(){(window.scrollY<t?ocultaElementosHeader:exibeElementosHeader)()});for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(t){var e=t.target.dataset.tabButton,e=document.querySelector(`[data-tab-id=${e}]`);hideAll(),e.classList.add("shows__list--is-active"),removeActiveButton(),t.target.classList.add("shows__tabs__button--is-active")});for(let t=0;t<o.length;t++)o[t].addEventListener("click",abreOuFechaResposta)});